// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract RecDapp {
    address public producer;
    uint public price;

    struct RecAccount {
        string data;
    }

    struct RequestAccount {
        address user;
        uint kwh;
    }

    mapping(address => RecAccount) public recAccounts;
    mapping(address => RequestAccount) public requestAccounts;

    constructor() {
        producer = msg.sender;
    }

    function issueRec(address recAccountAddress, string memory data) public {
        RecAccount storage recAccount = recAccounts[recAccountAddress];
        recAccount.data = data;
    }

    function verifyRec(address recAccountAddress, string memory data) public view returns (bool) {
        RecAccount storage recAccount = recAccounts[recAccountAddress];
        return keccak256(abi.encodePacked(recAccount.data)) == keccak256(abi.encodePacked(data));
    }

    function requestRec(address requestAccountAddress, uint kwh) public {
        RequestAccount storage requestAccount = requestAccounts[requestAccountAddress];
        requestAccount.user = msg.sender;
        requestAccount.kwh = kwh;
    }

    function issueAndSellRec(address recAccountAddress, string memory data, uint _price) public payable {
        require(msg.value >= _price, "Insufficient funds to purchase REC");

        RecAccount storage recAccount = recAccounts[recAccountAddress];
        recAccount.data = data;

        // Μετατροπή της διεύθυνσης του producer σε payable διεύθυνση
        address payable payableProducer = payable(producer);
        (bool sent, ) = payableProducer.call{value: _price}("");
        require(sent, "Failed to send Ether");
    }
}
